import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ToastrService } from 'ngx-toastr';
import { StudentService } from 'src/app/services/student.service';
@Component({
  selector: 'app-student',
  templateUrl: './student.component.html',
  styleUrls: ['./student.component.css']
})
export class StudentComponent {
  listStudents: any[]=[];
  accion = 'ADD';
  form: FormGroup;
  id: number | undefined;

  constructor(private fb: FormBuilder,
    private toastr: ToastrService,
    private _studentService: StudentService){
    this.form = this.fb.group({
      txtUsername:['',Validators.required],
      txtFirstName:['',Validators.required],
      txtLastName:['',Validators.required],
      txtAge:['',[Validators.required,Validators.minLength(3)]],
      txtCareer:['',Validators.required]
    })
  }

  ngOnInit(): void {
    this.getStudents();
  }

  getStudents(){
    this._studentService.getListStudent().subscribe(data => {
      console.log(data);
      this.listStudents = data;
    }, error =>{
      console.log(error);
    });
  }

  saveStudent(){

    const student: any ={
      Username: this.form.get('txtUsername')?.value,
      FirstName: this.form.get('txtFirstName')?.value,
      LastName: this.form.get('txtLastName')?.value,
      Age: this.form.get('txtAge')?.value,
      Career: this.form.get('txtCareer')?.value
    }

    if(this.id==undefined){
      this._studentService.saveStudent(student).subscribe(data => {
        this.toastr.success('The student was successfully registered', 'Registered student');
        this.getStudents();
        this.form.reset();
      },error =>{
        this.toastr.error('An error occurred while registering the student', 'Error');
        console.log(error);
      });
    }else{
      student.id = this.id;
      this._studentService.updateStudent(this.id, student).subscribe(data =>{
        this.form.reset();
        this.accion = 'ADD';
        this.id = undefined;
        this.toastr.info('The student was successfully updated','Updated student');
        this.getStudents();
      },error =>{
        this.toastr.error('An error occurred while updating the student', 'Error');
        console.log(error);
      });
    }
  }

  deleteStudent(id: number) {
    this._studentService.deleteStudent(id).subscribe(data=>{
      this.toastr.error('The student was successfully eliminated', 'Eliminated student');
      this.getStudents();
    }, error =>{
      console.log(error);
    });
  }

  editStudent (student: any){
    this.accion = 'UPDATE';
    this.id = student.id;

    this.form.patchValue({
      txtUsername: student.username,
      txtFirstName: student.firstName,
      txtLastName: student.lastName,
      txtAge: student.age,
      txtCareer: student.career
    })
  }

  validateCero(control) {
    const value = control.value;
    if (value <= 0) {
      return { negative: true };
    }
    return null;
  }
}
<div class="container mt-5">
    <div class="row">
        <div class="col-lg-8 offset-lg-2">
            <div class="card">
                <div class="card-body">
                    <h3 class="titulo">Students</h3>
                </div>
            </div>
        </div>
    </div>
    <div class="row mt-4">
        <div class="col-lg-6">
            <div class="card">
                <div class="card-body">
                    <h5 class="titulo"> {{ accion | uppercase}} STUDENT</h5>
                    <form [formGroup]="form" (ngSubmit)="saveStudent()">
                        <div class="input-group input-group-lg mb-3">
                            <span class="input-group-text">
                                <i class="fa-solid fa-user-pen"
                                [class.green-icon]="this.form.get('txtUsername')?.valid"
                                [class.red-icon]="this.form.get('txtUsername')?.invalid && this.form.get('txtUsername')?.touched"></i>
                            </span>
                            <input formControlName="txtUsername" type="text" class="form-control" placeholder="Username">
                        </div>
                        <div class="row">
                            <div class="col-lg-6">
                                <div class="input-group input-group-lg mb-3">
                                    <span class="input-group-text">
                                        <i class="fa-solid fa-circle-user"                                
                                        [class.green-icon]="this.form.get('txtFirstName')?.valid"
                                        [class.red-icon]="this.form.get('txtFirstName')?.invalid && this.form.get('txtFirstName')?.touched
                                        "></i>
                                    </span>
                                    <input formControlName="txtFirstName" type="text" class="form-control" placeholder="First Name">
                                </div>
                            </div>
                            <div class="col-lg-6">
                                <div class="input-group input-group-lg mb-3">
                                    <span class="input-group-text">
                                        <i class="fa-solid fa-circle-user"
                                        [class.green-icon]="this.form.get('txtLastName')?.valid"
                                        [class.red-icon]="this.form.get('txtLastName')?.invalid && this.form.get('txtLastName')?.touched
                                        "></i>
                                    </span>
                                    <input formControlName="txtLastName" type="text" class="form-control" placeholder="Last Name">
                                </div>
                            </div>
                        </div>
                        <div class="row">
                            <div class="col-lg-6">
                                <div class="input-group input-group-lg mb-3">
                                    <span class="input-group-text">                                        
                                        <i class="fa-solid fa-hashtag"
                                        [class.green-icon]="this.form.get('txtAge')?.valid"
                                        [class.red-icon]="this.form.get('txtAge')?.invalid && this.form.get('txtAge')?.touched
                                        "></i>
                                    </span>
                                    <input formControlName="txtAge" minlength="1" type="number" maxlength="3" class="form-control" placeholder="Age">
                                </div>
                            </div>
                            <div class="col-lg-6">
                                <div class="input-group input-group-lg mb-3">
                                    <span class="input-group-text">
                                        <i class="fa-solid fa-user-tie"
                                        [class.green-icon]="this.form.get('txtCareer')?.valid"
                                        [class.red-icon]="this.form.get('txtCareer')?.invalid && this.form.get('txtCareer')?.touched
                                        "></i>
                                    </span>
                                    <input formControlName="txtCareer" type="text" class="form-control" placeholder="Career">
                                </div>
                            </div>
                        </div>
                        <div class="d-grid gap-2">
                            <button [disabled]="this.form.invalid" type="submit" class="btn btn-success btn-lg">
                                <i class="fa-solid fa-database"></i>&nbsp; Add
                            </button>
                        </div>

                    </form>
                </div>
            </div>
        </div>
        <div class="col-lg-6">
            <div class="card">
                <div class="card-body">
                    <h5 class="titulo">LIST OF STUDENTS</h5>
                    <table class="table">
                        <tbody>
                            <tr *ngFor="let student of listStudents">
                                <td>{{student.username}}</td>
                                <td>{{student.firstName}}</td>
                                <td>{{student.lastName}}</td>
                                <td><i (click)="editStudent(student)" class="text-info far fa-solid fa-pen-to-square"></i></td>
                                <td><i (click)="deleteStudent(student.id)" class="text-danger far fa-solid fa-trash"></i></td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    </div>
</div>
